'use strict';

const Vulkan = require('../../Vulkan');
const UniformBufferSlotCache = require('../../helper/UniformBufferSlotCache');

const SlotBase = require('./SlotBase');

const objectBuffer3D = new Float32Array(256*4);

module.exports = SlotBase(new UniformBufferSlotCache(
	'2_dynamic',
	objectBuffer3D.byteLength,
	drawUnit => (
		drawUnit.entity.copyBoneStateToBuffer(objectBuffer3D),
		objectBuffer3D
	)
));
