'use strict';

const Vulkan = require('../../Vulkan');
const {UniformBufferSlot} = require('../../helper/Slot');

const buffer = new Float32Array(7);

module.exports = UniformBufferSlot(
	'2_dynamic',
	256,
	drawUnit => {
		const {bound} = drawUnit.material;
		buffer[0] = bound.minX;
		buffer[1] = bound.minY;
		buffer[2] = bound.minZ;
		buffer[3] = drawUnit.cameraIndex;
		buffer[4] = bound.maxX;
		buffer[5] = bound.maxY;
		buffer[6] = bound.maxZ;
		return buffer;
	}
);
