'use strict';

const Vulkan = require('../../Vulkan');
const VertexBufferSlotCache = require('../../helper/VertexBufferSlotCache');

const vertexData = new Float32Array(4);

function valueFn({frameRect:{width, height}, drawRect}){
	vertexData[0] = drawRect.x / width;
	vertexData[1] = drawRect.y / height;
	vertexData[2] = drawRect.width / width;
	vertexData[3] = drawRect.height / height;
	return vertexData;
}

module.exports = new VertexBufferSlotCache(
	100, vertexData.byteLength, valueFn
);
