'use strict';

const Vulkan = require('../Vulkan');

const pipelineTerrain = require('./pipelineTerrain');
const pipelineBound = require('./pipelineBound');
const pipelineCull = require('./pipelineCull');
const pipeline2D = require('./pipeline2D');
const pipelineColor = require('./pipelineColor');
const pipeline3D = require('./pipeline3D');
const pipeline3DWing = require('./pipeline3DWing');
const pipeline3DBillboard = require('./pipeline3DBillboard');
const pipelineWireframe = require('./pipelineWireframe');
const pipelineFilterBlur = require('./pipelineFilterBlur');

Vulkan.registerRenderPipeline('2d', pipeline2D);
Vulkan.registerRenderPipeline('3d', pipeline3D);
Vulkan.registerRenderPipeline('terrain', pipelineTerrain);
Vulkan.registerRenderPipeline('billboard', pipeline3DBillboard);
Vulkan.registerRenderPipeline('filter_blur', pipelineFilterBlur);
Vulkan.registerRenderPipeline('bound', pipelineBound);
Vulkan.registerRenderPipeline('3d_color', pipelineColor);
Vulkan.registerRenderPipeline('3d_wireframe', pipelineWireframe);
Vulkan.registerRenderPipeline('3d_wing', pipeline3DWing);
Vulkan.registerComputePipeline('cull', pipelineCull);
