'use strict';

const Vulkan = require('../Vulkan');
const createPipelineDesc3D = require('./createPipelineDesc3D');

module.exports = function(){
	const {device} = Vulkan;
	return device.createRenderPipeline(createPipelineDesc3D(
		['0', '2_dynamic', '2_dynamic', '3'],
		Vulkan.shader_3d.vertexCode,
		Vulkan.shader_3d.fragmentCode,
		'triangle-list',
		{
			srcFactor: 'one',
			dstFactor: 'one-minus-src-color',
			//dstFactor: 'one',
			operation: 'add'
		},
		false
	));
}
