'use strict';

const Vulkan = require('../Vulkan');

module.exports = function(){
	const {device} = Vulkan;
	return device.createComputePipeline({
		layout: device.createPipelineLayout({
			bindGroupLayouts:[
				Vulkan.layout_cull,//direct buffer
				Vulkan.layout_2,//world matrix
				Vulkan.layout_2,//camera
				Vulkan.layout_2,//bound
			]
		}),
		computeStage:{
			module: device.createShaderModule({
				code: Vulkan.shader_cull,
			}),
			entryPoint: 'main'
		}
	});
}