'use strict';

const Render2D = require('./Render2D');
const LabelDrawer = require('./LabelDrawer');
const BitmapDrawer = require('./BitmapDrawer');

function createRender2D(){
	const render = new Render2D();
	render.register('label2d', LabelDrawer);
	render.register('bitmap', BitmapDrawer);
	render.register('bitmap3d', BitmapDrawer);
	return render;
}

module.exports = createRender2D;
