'use strict';

function play(src, callback){
	const audio = document.createElement('audio');
	audio.setAttribute('autoplay', 'autoplay');
	audio.setAttribute('controls', 'true');
	audio.addEventListener('ended', evt => callback());
	if(typeof src != 'string'){
		const blob = new Blob([src], {type: 'audio/mp3'});
		src = URL.createObjectURL(blob);
	}
	audio.src = src;
	return audio;
}

exports.play = play;
