
function loadBlob(blob, callback){
	let reader = new FileReader();
	reader.addEventListener("loadend", () => callback(reader.result));
	reader.readAsArrayBuffer(blob);
}

function supportDrag(callback){
	window.ondragover = function(evt){
		evt.preventDefault();
		return false;
	}
	window.ondrop = function(evt){
		evt.preventDefault();
		callback(Array.from(evt.dataTransfer.files, v => v.path));
		return false;
	}
}

exports.loadBlob = loadBlob;
exports.supportDrag = supportDrag;