const {readFileSync, writeFileSync} = require('fs');

function cast(path){
	let text = readFileSync(path, 'utf8');
	text = text.slice(text.indexOf(',') + 1);
	writeFileSync(path + '.png', Buffer.from(text, 'base64'));
}

process.argv.slice(2).forEach(cast);
